import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Loading from './Loading';
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom';

export default class News extends Component {
    static defaultProps = {
        pageSize: 9,
        country: 'in'
    }

    static propTypes = {
        pageSize: PropTypes.number,
        country: PropTypes.string,
        apiKey: PropTypes.string,
    }

    constructor() {
        super();
        this.state = {
            articles: [],
            loading: true,
            page: 1,
            totalResults: 0
        }
    }

    componentDidMount() {
        this.updateNews();
    }

    updateNews = ()=>{
        console.log(this.state.page)
        this.setState({
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.apiKey}&pageSize=${this.props.pageSize}&page=${this.state.page}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                totalResults: response.totalResults
            })
        }).then(() => {
            this.setState({
                loading: false
            })
        })
        console.log(this.state.page)
    }

    handleNextClick = () => {
        console.log(this.state.page);
        this.setState({                   // ***imp -> setState() is usually asynchronous. State ek dam se update nahi ho jaati
            page: this.state.page+1             // setState() function ka 2nd argument ek callback function hota hai which is only executed after the state has been updated
        }, ()=>{
            this.updateNews();
        })
    }

    handlePrevClick = async() => {
        await this.setState({
            page: this.state.page-1
        })
        this.updateNews();
    }


    render() {
        return (
            <>
                <div className="container my-4" id="top">
                    <h1 className="my-4 text-center">NewsMonkey - Top Headlines</h1>
                    <div className="container d-flex justify-content-center my-3">
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" onClick={document.title=this.props.category} to="/business">Business</Link>
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" to="/entertainment">Entertainment</Link>
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" to="/health">Health</Link>
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" to="/science">Science</Link>
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" to="/sports">Sports</Link>
                        <Link className="btn btn-primary btn-lg btn-sm border-pill mx-2 rounded-pill" role="button" to="/technology">Technology</Link>
                    </div>
                    {this.state.loading ?
                        <Loading className="w-100" /> :
                        <div className="row" style={{alignItems:'center'}}>
                            {this.state.articles.map((elem) => {
                                return <div className="col-md-4 my-3" key={elem.url}>
                                    <NewsItem author={elem.author} url={elem.url} source={elem.source.name} date={elem.publishedAt} title={elem.title.slice(0, 45)} description={elem.description && elem.description.slice(0, 88)} imgUrl={elem.urlToImage} newsUrl={elem.url} />
                                </div>
                            })}
                        </div>
                    }
                </div>
                <div className="container d-flex justify-content-between my-5">
                    <button type="button" className={`btn btn-secondary ${this.state.page === 1 ? 'invisible' : 'visible'}`} onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button type="button" className="btn btn-secondary rounded-pill"><a href="#top" className="text-light"><i className="fa-solid fa-arrow-up"></i></a></button>
                    <button type="button" id="next-btn" className={`btn btn-secondary ${this.state.page >= Math.ceil(this.state.totalResults / this.props.pageSize) ? 'invisible' : 'visible'}`} onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </>
        )
    }
}
