import './App.css';

import React, { Component } from 'react'
import Navbar from './components/Navbar';
import News from './components/News';
import LoadingBar from 'react-top-loading-bar'

import {
  BrowserRouter as Router,
  Routes,
  Route
} from 'react-router-dom';

export default class App extends Component {
  apiKey = process.env.REACT_APP_API_KEY;

  state = {
    progress: 0
  }

  setProgress = (progress) => {
    this.setState({
      progress: progress
    })
  }

  render() {
    return (
      <>
        <Router>
          <Navbar />
          <LoadingBar color='#f11946' progress={this.state.progress} waitingTime={800} />
          <Routes>    {/* React ko lagega ki component toh phele se hi mount ho chuka hai toh phir se kyun krna hai -> Woh replace nahi karega component ko(update nahi karega) */}
            {/* Toh main forced re-mount kr sakta hun -> By giving a unique key to each */}
            <Route exact path="/" element={<News setProgress={this.setProgress} key="general" pageSize={12} apiKey={this.apiKey} country="in" category="general" />} />
            <Route exact path="/science" element={<News setProgress={this.setProgress} key="science" pageSize={12} apiKey={this.apiKey} country="in" category="science" />} />
            <Route exact path="/business" element={<News setProgress={this.setProgress} key="business" pageSize={12} apiKey={this.apiKey} country="in" category="business" />} />
            <Route exact path="/entertainment" element={<News setProgress={this.setProgress} key="entertainment" pageSize={12} apiKey={this.apiKey} country="in" category="entertainment" />} />
            <Route exact path="/health" element={<News setProgress={this.setProgress} key="health" pageSize={12} apiKey={this.apiKey} country="in" category="health" />} />
            <Route exact path="/sports" element={<News setProgress={this.setProgress} key="sports" pageSize={12} apiKey={this.apiKey} country="in" category="sports" />} />
            <Route exact path="/technology" element={<News setProgress={this.setProgress} key="technology" pageSize={12} apiKey={this.apiKey} country="in" category="technology" />} />
          </Routes>
        </Router>
      </>
    )
  }
}

