import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Loading from './Loading';
import PropTypes from 'prop-types'

export default class News extends Component {
    static defaultProps = {
        pageSize: 9,
        country: 'in'
    }

    static propTypes = {
        pageSize: PropTypes.number,
        country: PropTypes.string,
        apiKey: PropTypes.string,
    }

    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0,
            category: 'general'
        }
    }

    componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=${this.props.apiKey}&pageSize=${this.props.pageSize}&page=${this.state.page}`;
        let p = fetch(url);
        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                totalResults: response.totalResults
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }

    handleNextClick = () => {
        this.setState({
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=${this.props.apiKey}&pageSize=${this.props.pageSize}&page=${this.state.page + 1}&category=${this.state.category}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page + 1
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }

    handlePrevClick = () => {
        this.setState({
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=${this.props.apiKey}&pageSize=${this.props.pageSize}&page=${this.state.page - 1}&category=${this.state.category}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page - 1
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }

    handleCategoryClick = async (category)=>{
        this.setState({
            category: category,
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=${this.props.apiKey}&pageSize=${this.props.pageSize}&page=${this.state.page}&category=${this.state.category}`;
        let p = fetch(url);
        let response = (await p).json();
        this.setState({
            articles: response.articles,
            totalResults: response.totalResults,
            loading: false
        })
    }


    render() {
        return (
            <>
                <div className="container my-4" id="top">
                    <h1 className="my-4 text-center">NewsMonkey - Top Headlines</h1>
                    <div className="container d-flex justify-content-center my-3">
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill border-dark" onClick={this.handleCategoryClick('business')}>Business</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">Entertainment</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">Health</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">General</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">Science</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">Sports</button>
                        <button type="button" class="btn btn-success btn-sm mx-2 rounded-pill">Technology</button>
                    </div>
                    {this.state.loading ?
                        <Loading className="w-100" /> :
                        <div className="row">
                            {this.state.articles.map((elem) => {
                                return <div className="col-md-4 my-3" key={elem.url}>
                                    <NewsItem title={elem.title.slice(0, 45)} description={elem.description && elem.description.slice(0, 88)} imgUrl={elem.urlToImage} newsUrl={elem.url} />
                                </div>
                            })}
                        </div>
                    }
                </div>
                <div className="container d-flex justify-content-between my-5">
                    <button type="button" className={`btn btn-secondary ${this.state.page === 1 ? 'invisible' : 'visible'}`} onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button type="button" className="btn btn-secondary rounded-pill"><a href="#top" className="text-light"><i className="fa-solid fa-arrow-up"></i></a></button>
                    <button type="button" id="next-btn" className={`btn btn-secondary ${this.state.page >= Math.ceil(this.state.totalResults / this.props.pageSize) ? 'invisible' : 'visible'}`} onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </>
        )
    }
}
