import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Loading from './Loading';

export default class News extends Component {
    constructor() {
        super();
        this.state = {
            articles: [],
            loading: true,
            page: 1,
            totalResults:0
        }
    }

    componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page}`;
        let p = fetch(url);
        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                totalResults: response.totalResults
            })
        })
    }

    handleNextClick = ()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page+1}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page+1
            })
        })
    }

    handlePrevClick = ()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page-1}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page-1
            })
        })
    }


    render() {
        return (
            <>
                <Loading />
                
            </>
        )
    }
}
