import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Loading from './Loading';

export default class News extends Component {
    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0
        }
    }

    componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page}`;
        let p = fetch(url);
        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                totalResults: response.totalResults
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }

    handleNextClick = () => {
        this.setState({
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page + 1}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page + 1
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }

    handlePrevClick = () => {
        this.setState({
            loading: true
        })
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=${this.props.pageSize}&page=${this.state.page - 1}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page - 1
            })
        }).then(()=>{
            this.setState({
                loading: false
            })
        })
    }


    render() {
        return (
            <>
                <div className="container my-4" id="top">
                    <div style={{display:'flex', flexDirection:'column', flex:1}}>
                        <h1 className="my-4 text-center">NewsMonkey - Top Headlines</h1>
                        {this.state.loading ?
                            <Loading style={{flex:1}} /> :
                            <div className="row">
                                {this.state.articles.map((elem) => {
                                    return <div className="col-md-4 my-3" key={elem.url}>
                                        <NewsItem title={elem.title.slice(0, 45)} description={elem.description && elem.description.slice(0, 88)} imgUrl={elem.urlToImage} newsUrl={elem.url} />
                                    </div>
                                })}
                            </div>
                        }
                    </div>
                </div>
                <div className="container d-flex justify-content-between my-5">
                    <button type="button" className={`btn btn-secondary ${this.state.page === 1 ? 'invisible' : 'visible'}`} onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button type="button" className="btn btn-secondary rounded-pill"><a href="#top" className="text-light"><i className="fa-solid fa-arrow-up"></i></a></button>
                    <button type="button" id="next-btn" className={`btn btn-secondary ${this.state.page >= Math.ceil(this.state.totalResults / this.props.pageSize) ? 'invisible' : 'visible'}`} onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </>
        )
    }
}
