import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page: 1
        }
    }

    componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12";
        let p = fetch(url);
        p.then((response)=>{
            console.log(response.ok)
            return response.json();
        }).then((response)=>{
            console.log(response);
            const articlesWithImg = response.articles.filter((elem)=>{
                return (elem.urlToImage !== null)
            })
            this.setState({
                articles : articlesWithImg
            })
        })
    }


    render() {
        return (
            <div className="container my-4">
                <h1 className="my-4">NewsMonkey - Top Headlines</h1>
                <div className="row">
                    {this.state.articles.map((elem) => {
                        return <div className="col-md-4 my-3" key={elem.url}>
                            <NewsItem title={elem.title.slice(0, 45)} description={elem.description && elem.description.slice(0, 88)} imgUrl={elem.urlToImage} newsUrl={elem.url} />
                        </div>
                    })}
                </div>
            </div>
        )
    }
}
