import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults:0
        }
    }

    componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=12&page=${this.state.page}`;
        let p = fetch(url);
        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                totalResults: response.totalResults
            })
        })
    }

    handleNextClick = ()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=12&page=${this.state.page+1}`;
        let p = fetch(url);

        if(Math.ceil(this.state.page >= this.state.totalResults/12))
        {
            document.getElementById('next-btn').style.visibility="hidden";
        }

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page+1
            })
        })
    }

    handlePrevClick = ()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ca98c8464d074024aaac3fd4b6a32a12&pageSize=12&page=${this.state.page-1}`;
        let p = fetch(url);

        p.then((response) => {
            return response.json();
        }).then((response) => {
            this.setState({
                articles: response.articles,
                page: this.state.page-1
            })
        })
    }



    render() {
        return (
            <>
                <div className="container my-4" id="top">
                    <h1 className="my-4">NewsMonkey - Top Headlines</h1>
                    <div className="row">
                        {this.state.articles.map((elem) => {
                            return <div className="col-md-4 my-3" key={elem.url}>
                                <NewsItem title={elem.title.slice(0, 45)} description={elem.description && elem.description.slice(0, 88)} imgUrl={elem.urlToImage} newsUrl={elem.url} />
                            </div>
                        })}
                    </div>
                </div>
                <div className="container d-flex justify-content-between my-5">
                    <button type="button" class={`btn btn-secondary ${this.state.page===1?'invisible':'visible'}`} onClick={this.handlePrevClick}>&larr; Previous</button>
                    <button type="button" id="next-btn" class="btn btn-secondary" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </>
        )
    }
}
