import React, { Component } from 'react'

export default class NewsItem extends Component {
    render() {
        let { title, description, imgUrl, newsUrl, author, date } = this.props;
        return (
            <div className="card mx-4" style={{boxShadow:'2px 2px 20px 2px grey', position:'relative'}}>
                <img src={imgUrl?imgUrl:"https://scitechdaily.com/images/Sub-Atomic-Particle-Quantum-Physics-Art-Illustration.jpg"} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{title}...</h5>
                    <p className="card-text">{description}...</p>
                    <p className="card-text my-3"><small className="text-body-secondary">Published by <b>{author}</b> on <b className="text-info">{date}</b></small></p>
                    <a href={newsUrl} rel="noreferrer" target="_blank" className="btn btn-sm btn-primary">Read More...</a>
                    {}
                    <span class="badge rounded-pill text-bg-primary" style={{position:'absolute', top:'4.5px', right:'4.5px'}}>Latest</span>
                </div>
            </div>
        )
    }
}
