import React, { Component } from 'react'

export default class NewsItem extends Component {
    render() {
        let { title, description, imgUrl, newsUrl, author, date, source, url } = this.props;
        return (
            <div className="card mx-4" style={{boxShadow:'2px 2px 20px 2px grey', position:'relative'}}>
                <img src={imgUrl?imgUrl:"https://scitechdaily.com/images/Sub-Atomic-Particle-Quantum-Physics-Art-Illustration.jpg"} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{title}...</h5>
                    <p className="card-text">{description}...</p>
                    <p className="card-text my-3 bg-dark rounded p-2"><small className="text-body-secondary"><span className="text-light">Published by <b>{author===null?"Unknown":author}</b> on </span><b className="text-info">{new Date(date).toGMTString()}</b></small></p>
                    <a href={newsUrl} rel="noreferrer" target="_blank" className="btn btn-sm btn-primary">Read More...</a>
                    <a href={url} target="_blank" class="text-decoration-none badge rounded-pill text-bg-primary" style={{position:'absolute', top:'4.5px', right:'4.5px'}}>{source}</a>
                </div>
            </div>
        )
    }
}
